// Pega o primeiro item que chega no nó
const item = $input.item;

// Pega o conteúdo base64 da propriedade 'content'
const base64Content = item.json.content;

// Limpa possíveis espaços ou quebras de linha (como medida de segurança)
const cleanedBase64 = base64Content.replace(/\s/g, '');

// Decodifica o conteúdo de Base64 para texto legível (UTF-8)
const decodedContent = Buffer.from(cleanedBase64, 'base64').toString('utf8');

// Cria um novo campo chamado 'conteudoDecodificado' no seu item
item.json.conteudoDecodificado = decodedContent;

// Retorna o item já com o novo campo para o próximo nó
return item;
